mapper.select:
		 SELECT 
		 	
		 FROM life_cost_detail 
		 WHERE 1 = 1
		 
        	AND cost_detail_no = #{cost_detail_no}
         
         
        	AND cost_type = #{cost_type}
         
         
        	AND goods_name like #{goods_name}
         
         
        	AND pay_way = #{pay_way}
         
         
        	AND create_time between #{start_time} and #{end_time}
         
         order by create_time desc
