mapper.select:
	select 
		 
	from life_waist
	where user_no = #{userNo, jdbcType=VARCHAR}
	  and year = #{year, jdbcType=INTEGER}
      and month = #{month, jdbcType=INTEGER}
      order by day

	select 
		 
	from life_waist
	where user_no = #{userNo, jdbcType=VARCHAR}
      and measurement_time between #{startTime, jdbcType=TIMESTAMP} and #{endTime, jdbcType=TIMESTAMP}
      order by month, day
